pragma solidity ^0.4.17;

contract ProductFactory {
    address[] deployedProducts;

    function createProduct(string name) public {
        address newProduct = new Product(name, msg.sender);
        deployedProducts.push(newProduct);
    }

    function getDeployedProducts() public view returns(address[]) {
        return deployedProducts;
    }
}

// A product in the supply chain
contract Product {
    address issuer;
    string name;
    string description;
    Stage[] stages; // all the stages the product has gone through

    // A stage in the product's supply chain cycle
    struct Stage {
        address issuer;
        uint256 startDate;
        uint256 endDate;
        string description;
    }

    function Product(string _name, address _issuer) public {
        issuer = _issuer;
        name = _name;
    }

    // Add a stage to the product's chain
    function addStage(string _description) public {
        Stage memory stage = Stage({
            issuer: msg.sender, 
            description: _description,
            startDate: 123,
            endDate: 123
        });

        stages.push(stage);
    } 
}
